import React, { useEffect } from "react";
import { useQuery } from "react-query";
import { Dots } from "react-activity";
import "react-activity/dist/library.css";

import { useNavigate, useLocation, useParams } from "react-router-dom";

function Home() {
  const navigate = useNavigate();
  const { username } = useParams();
  const getNotes = async () => {
    const res = await fetch("http://localhost:5000/notes");
    console.log(res.json);
    return res.json();
  };
  useEffect(() => {
    refetchNotes();
  }, []);
  // Using the hook
  const {
    data,
    error,
    isLoading,
    refetch: refetchNotes,
  } = useQuery("notes", getNotes);

  if (error) return <div>Request Failed</div>;
  if (isLoading)
    return (
      <div className=" h-screen flex flex-col w-full  items-center pt-48 bg-sky-800">
        <Dots color={"white"} />
      </div>
    );
  return (
    <div className="h-screen flex items-center justify-start flex-col pt-4  bg-sky-800">
      <div className="w-3/4 h-full flex flex-col overflow-y-scroll no-scrollbar p-4">
        {data.length === 0 && (
          <p className=" text-center text-sky-100 pt-48">{username}</p>
        )}
        {data.map((note) => {
          return (
            <div
              className="flex flex-row h-24 border-b border-white w-full rounded-lg bg-sky-100 shadow-2xl shadow-sky-100/30 p-2 my-2"
              onClick={() => {
                navigate(`/note/${note.noteid}`);
              }}
            >
              <p className=" text-sky-900">
                {note.noteid}: {note.text}
              </p>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Home;
