import React from "react";
import "react-activity/dist/library.css";
import { useSelector } from "react-redux";
import { useQuery } from "react-query";

function Report() {
  const username = useSelector((state) => state.user.username);

  const getUsers = async () => {
    const res = await fetch(`http://localhost:5000/users`);
    return res.json();
  };
  const { data: users, isLoading: isUsersLoading } = useQuery(
    "users",
    getUsers
  );

  return (
    <div className="h-screen flex items-center justify-start flex-col pt-4  bg-white">
      <div>
        <p>Total Notes: 0</p>
        <p>Total Likes: 0</p>
        <p>Total Users: {users?.length}</p>
        <p>Average Likes/Note</p>
        <p>Average Likes/User</p>
      </div>
      <div className="bg-primary-100 w-full justify-center items-center flex flex-col">
        <p className="text-xl">Data By Users:</p>
        <div>
          {users?.map((user) => (
            <div className="flex flex-col bg-primary-50 w-full justify-evenly">
              <p>{user.username}</p>
              <div className="flex flex-row w-full h-full">
                <p>Total Notes: 0</p>
                <p>Total Likes: 0</p>
                <p>Average Likes/Note</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Report;
