import React from "react";
import { Link, useParams, useNavigate } from "react-router-dom";

import { useQuery } from "react-query";

function Note() {
  const navigate = useNavigate();

  const { id } = useParams();
  const deleteNote = () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = "";

    var requestOptions = {
      method: "DELETE",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    fetch(`http://localhost:5000/notes/${id}`, requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));

    navigate("/allnotes");
  };
  const getNote = async () => {
    const res = await fetch(`http://localhost:5000/notes/${id}`);
    console.log(res.json);
    return res.json();
  };
  // Using the hook
  const { data, error, isLoading } = useQuery("note", getNote);
  return (
    <div className="h-screen flex items-center justify-start flex-col pt-10">
      <div className="flex flex-row border-b border-black justify-center pb-4 px-10">
        <h1 onClick={() => navigate("/")} className="text-3xl">
          Welcome to Noter
        </h1>
      </div>
      <div className="w-full flex flex-row justify-evenly pt-10">
        <Link to={"/allnotes"}>All Notes</Link>
        <Link to={"/create"}>New Note</Link>
      </div>
      {!isLoading && !error && data && (
        <div className="flex flex-col h-3/4 w-3/4  mt-10 p-5 border border-black">
          <p>
            Note {data[0].noteid}:<br /> {data[0].text}
          </p>
        </div>
      )}
      <div onClick={deleteNote}>
        <p>Delete Note</p>
      </div>
    </div>
  );
}

export default Note;
