import React, { useEffect } from "react";
import { useQuery } from "react-query";
import { Dots } from "react-activity";
import "react-activity/dist/library.css";
import { useState } from "react";

import { useNavigate } from "react-router-dom";

function Home() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const createUser = async (username, password) => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      username,
      password,
    });

    var requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    fetch("http://localhost:5000/users/", requestOptions)
      .then((response) => response.text())
      .then((result) => {
        alert("" + result);
      })
      .catch((error) => {
        alert("error");
      });
  };

  return (
    <div className="h-screen flex items-center justify-start flex-col pt-4  bg-sky-800">
      <div className="w-3/4 h-full flex flex-col overflow-y-scroll no-scrollbar p-4 space-y-2">
        <input
          className="h-12 border rounded-xl p-2"
          type="text"
          placeholder="Username"
          onChange={(e) => {
            setUsername(e.target.value);
          }}
        />
        <input
          className="h-12 border rounded-xl p-2"
          type="password"
          placeholder="Password"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        />
        <button
          className="h-12 border rounded-xl p-2 bg-sky-100"
          onClick={() => {
            if (username !== "" && password !== "")
              createUser(username, password);
          }}
        >
          Submit
        </button>
      </div>
    </div>
  );
}

export default Home;
