import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

import { useQuery } from "react-query";

function Note() {
  const navigate = useNavigate();
  const [text, setText] = useState("");
  const [updated, setUpdated] = useState(false);
  const [showUsers, setShowUsers] = useState(false);

  const { id } = useParams();
  const deleteNote = async () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = "";

    var requestOptions = {
      method: "DELETE",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    await fetch(`http://localhost:5000/notes/${id}`, requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));

    navigate("/");
  };
  const editNote = async () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      text: text,
    });

    var requestOptions = {
      method: "PUT",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    await fetch(`http://localhost:5000/notes/${id}`, requestOptions)
      .then((response) => {
        setUpdated(true);
        response.text();
      })
      .then((result) => {
        setUpdated(true);
        console.log(result);
      })
      .catch((error) => console.log("error", error));
  };
  const getNote = async () => {
    const res = await fetch(`http://localhost:5000/notes/${id}`);
    console.log(res.json);
    return res.json();
  };
  // Using the hook
  const getUsers = async () => {
    const res = await fetch(`http://localhost:5000/users`);
    return res.json();
  };
  const { data, isLoading } = useQuery("note", getNote);
  const { data: users, isLoading: isUsersLoading } = useQuery(
    "users",
    getUsers
  );
  useEffect(() => {
    if (data && data[0] && data[0].text) setText(data[0].text);
  }, [data]);
  return (
    <div className="h-screen flex items-center justify-start flex-col pt-10 bg-sky-800">
      {!isLoading && data[0] && (
        <div className="flex flex-col h-1/2 w-3/4">
          <textarea
            className="h-full border rounded-xl p-2"
            onChange={(e) => {
              setUpdated(false);
              setText(e.target.value);
            }}
            value={text}
          ></textarea>
          <div
            onClick={() => {
              if (text === "") return;
              else editNote();
            }}
            className={`flex flex-row w-full justify-center items-center rounded-lg mt-4 ${
              text === "" ? "bg-slate-200" : "bg-sky-100"
            }`}
          >
            <p className={`${text === "" ? "text-slate-400" : "text-sky-800"}`}>
              {updated ? "Success!" : "Update Note!"}
            </p>
          </div>
          <div
            onClick={() => {
              deleteNote();
            }}
            className={`flex flex-row w-full justify-center items-center rounded-lg mt-4 ${"bg-red-300"}`}
          >
            <p className={`text-red-800`}>Delete Note</p>
          </div>
          <div
            onClick={() => {
              setShowUsers(true);
            }}
            className={`flex flex-row w-full justify-center items-center rounded-lg mt-4 ${"bg-green-300"}`}
          >
            <p className={`text-green-800`}>Share Note</p>
          </div>
        </div>
      )}
      {showUsers && (
        <div className="bg-sky-200/90 w-[80%] h-[70%] absolute rounded-xl flex flex-col justify-center items-center">
          <div className="h-[15%] w-full flex flex-row items-center justify-center">
            <p className="text-sky-800 text-xl text-center">Share Note</p>
            <button
              onClick={() => setShowUsers(false)}
              className="text-xs absolute right-6 text-sky-900 top-2 bg-sky-100 h-6 w-6 rounded-full justify-center items-center text-center"
            >
              x
            </button>
          </div>
          <div className="h-[85%] w-[95%] bg-white justify-center items-center flex rounded-xl flex-col">
            {isUsersLoading && <p>Loading...</p>}
            {!isUsersLoading &&
              users &&
              users.map((user) => {
                return (
                  <div className="w-full h-16 bg-sky-100 rounded-xl justify-center items-center flex flex-row">
                    {user.username}
                  </div>
                );
              })}
          </div>
        </div>
      )}
    </div>
  );
}

export default Note;
