import React from "react";
import { useLocation } from "react-router-dom";
import { useSelector } from "react-redux";

import { Link } from "react-router-dom";

function Header() {
  const location = useLocation();
  const username = useSelector((state) => state.user.username);

  return (
    location.pathname !== "/login" &&
    location.pathname !== "/createAccount" && (
      <div className="w-full flex flex-row justify-evenly pt-4 border-b border-white pb-2">
        <p className="text-sky-900">Welcome, {username}</p>
        <Link to={"/"}>
          <div className="p-1 bg-sky-100 rounded-lg mt-3">
            <p className="text-sky-900">All Notes</p>
          </div>
        </Link>
        <h1 className="text-4xl text-sky-800">Noter</h1>
        <Link to={"/create"}>
          <div className="p-1 bg-sky-100 rounded-lg mt-3">
            <p className="text-sky-900">+ New Note </p>
          </div>
        </Link>
      </div>
    )
  );
}

export default Header;
