import React from "react";
import { useQuery } from "react-query";
import { Link, useNavigate } from "react-router-dom";

function Home() {
  const navigate = useNavigate();
  const getNotes = async () => {
    const res = await fetch("http://localhost:5000/notes");
    console.log(res.json);
    return res.json();
  };
  // Using the hook
  const { data, error, isLoading } = useQuery("randomFacts", getNotes);

  if (error) return <div>Request Failed</div>;
  if (isLoading) return <div>Loading...</div>;
  return (
    <div className="h-screen flex items-center justify-start flex-col pt-10">
      <div className="flex flex-row border-b border-white justify-center pb-4 px-10">
        <h1 onClick={() => navigate("/")} className="text-3xl">
          Welcome to Noter
        </h1>
      </div>
      <div className="w-full flex flex-row justify-evenly pt-10">
        <Link to={"/"}>All Notes</Link>
        <Link to={"/create"}>New Note</Link>
      </div>
      <div className="flex flex-col h-3/4 w-3/4  mt-10 border border-black">
        <div className="w-full h-[10%] flex justify-center items-center border-b border-black">
          <p>All Notes</p>
        </div>
        <div className="w-full h-[90%] flex flex-col  border-b border-black overflow-y-scroll no-scrollbar">
          {data.map((note) => {
            return (
              <div
                className="flex flex-row h-24 border-b border-black w-full rounded-lg bg-sky-800 p-2"
                onClick={() => {
                  navigate(`/note/${note.noteid}`);
                }}
              >
                <p className=" text-white">
                  {note.noteid}: {note.text} ...
                </p>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Home;
