import React from "react";
import { useQuery } from "react-query";
import { Link, useNavigate } from "react-router-dom";

function Home() {
  const navigate = useNavigate();
  const getNotes = async () => {
    const res = await fetch("http://localhost:5000/notes");
    console.log(res.json);
    return res.json();
  };
  // Using the hook
  const { data, error, isLoading } = useQuery("randomFacts", getNotes);

  if (error) return <div>Request Failed</div>;
  if (isLoading) return <div>Loading...</div>;
  return (
    <div className="h-screen flex items-center justify-start flex-col pt-4 bg-sky-900">
      <div className="flex flex-row  justify-center pb-4">
        <h1 onClick={() => navigate("/")} className="text-3xl text-white">
          Noter
        </h1>
      </div>
      <div className="w-3/4 flex flex-row justify-evenly pt-4 border-b border-white pb-2">
        <Link to={"/"}>
          <div className="p-1 bg-sky-100 rounded-lg">
            <p className="text-sky-900">All Notes</p>
          </div>
        </Link>
        <Link to={"/create"}>
          <div className="p-1 bg-sky-100 rounded-lg">
            <p className="text-sky-900">+ New Note </p>
          </div>
        </Link>
      </div>
      <div className="w-3/4 h-full flex flex-col overflow-y-scroll no-scrollbar p-2">
        {data.map((note) => {
          return (
            <div
              className="flex flex-row h-24 border-b border-white w-full rounded-lg bg-sky-100 p-2 my-1"
              onClick={() => {
                navigate(`/note/${note.noteid}`);
              }}
            >
              <p className=" text-sky-900">
                {note.noteid}: {note.text} ...
              </p>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Home;
