import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

import { useQuery } from "react-query";

function Note() {
  const navigate = useNavigate();
  const [text, setText] = useState("");

  const { id } = useParams();
  const deleteNote = () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = "";

    var requestOptions = {
      method: "DELETE",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    fetch(`http://localhost:5000/notes/${id}`, requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));

    navigate("/allnotes");
  };
  const editNote = () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      text: text,
    });

    var requestOptions = {
      method: "PUT",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    fetch(`http://localhost:5000/notes/${id}`, requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };
  const getNote = async () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      text: "updatema",
    });

    var requestOptions = {
      method: "GET",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    fetch("http://localhost:5000/notes/89", requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
    return res.json();
  };
  // Using the hook
  const { data, error, isLoading } = useQuery("note", getNote);
  return (
    <div className="h-screen flex items-center justify-start flex-col pt-10 bg-sky-800">
      <div className="flex flex-col h-1/2 w-3/4">
        <textarea
          className="h-full border rounded-xl p-2"
          onChange={(e) => setText(e.target.value)}
          placeholder={data[0].text}
        ></textarea>
        <div
          onClick={() => {
            if (text === "") return;
            else editNote();
          }}
          className={`flex flex-row w-full justify-center items-center rounded-lg mt-4 ${
            text === "" ? "bg-slate-200" : "bg-sky-100"
          }`}
        >
          <p className={`${text === "" ? "text-slate-400" : "text-sky-800"}`}>
            Update Note!
          </p>
        </div>
        <div
          onClick={() => {
            deleteNote();
          }}
          className={`flex flex-row w-full justify-center items-center rounded-lg mt-4 ${"bg-red-300"}`}
        >
          <p className={`text-red-800`}>Delete Note</p>
        </div>
      </div>
    </div>
  );
}

export default Note;
