import React from "react";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";

function AllNotes() {
  const getNotes = async () => {
    const res = await fetch("http://localhost:5000/notes");
    console.log(res.json);
    return res.json();
  };
  // Using the hook
  const { data, error, isLoading } = useQuery("randomFacts", getNotes);

  if (error) return <div>Request Failed</div>;
  if (isLoading) return <div>Loading...</div>;
  return (
    <div className="h-screen flex items-center justify-start flex-col">
      <div className="w-full flex flex-row justify-evenly pt-10">
        <Link to={"/allnotes"}>All Notes</Link>
        <Link to={"/allnotes"}>My Notes</Link>
        <Link to={"/create"}>New Note</Link>
      </div>
      <div className="flex flex-col h-full w-3/4  mt-10 border border-black">
        <div className="w-full h-[10%] flex justify-center items-center border-b border-black">
          <p>All Notes</p>
        </div>
        <div className="w-full h-[90%] flex flex-col  border-b border-black overflow-scroll">
          {data.map((note) => {
            return (
              <div className="flex flex-row h-[20%] border-b border-black w-full items-center">
                <p>{note.text}</p>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default AllNotes;
