import React from "react";
import { useMutation } from "react-query";
import { Link, useNavigate } from "react-router-dom";

function Create() {
  const navigate = useNavigate();
  const [text, setText] = React.useState("");
  const [id, setId] = React.useState(-1);
  const handleSubmit = () => {
    if (text === "") return;
    mutate();
  };

  const createNote = async () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      text: text,
    });

    var requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    const response = await fetch("http://localhost:5000/notes", requestOptions);

    const result = response.json();
    setId(result.noteid);
    return result;
  };
  // Using the hook
  const { mutate, error, isLoading } = useMutation(createNote, {
    onSuccess: (data) => {
      navigate(`/note/${id}`);
    },
    onError: () => {
      alert("ERROR! Note not created.");
    },
  });

  return (
    <div className="h-screen flex items-center justify-start flex-col pt-10">
      <div className="flex flex-row border-b border-black justify-center pb-4 px-10">
        <h1 onClick={() => navigate("/")} className="text-3xl">
          Welcome to Noter
        </h1>
      </div>
      <div className="w-full flex flex-row justify-evenly pt-10">
        <Link to={"/allnotes"}>All Notes</Link>
        <Link to={"/create"}>New Note</Link>
      </div>
      <div className="flex flex-col h-1/2 w-3/4 bg-slate-100">
        <input
          className="h-full border border-black"
          onChange={(e) => setText(e.target.value)}
          placeholder="Type Here!"
        ></input>
        <div
          onClick={handleSubmit}
          className="flex flex-row h-10 w-full bg-slate-300 justify-center items-center"
        >
          <p>Create Note!</p>
        </div>
      </div>
    </div>
  );
}

export default Create;
