import React from "react";
import "react-activity/dist/library.css";
import { useSelector } from "react-redux";
import { useQuery } from "react-query";

function Report() {
  const getUsers = async () => {
    const res = await fetch(`http://localhost:5000/usersreport`);
    return res.json();
  };
  const { data: users, isLoading: isUsersLoading } = useQuery(
    "users",
    getUsers
  );
  const getReport = async () => {
    const res = await fetch(`http://localhost:5000/report`);
    return res.json();
  };
  const { data: report, isLoading: isReportLoading } = useQuery(
    "report",
    getReport
  );

  return (
    <div className="h-screen w-full flex items-center justify-start flex-col p-4  bg-primary-200">
      <div className="flex flex-col justify-center items-center w-full bg-primary-50 rounded-lg p-2">
        <p className="text-xl border-b border-black">Total Data:</p>

        <p>Total Notes: {report?.notes}</p>
        <p>Total Users: {report?.users}</p>
        <p>Total Likes: {report?.likes}</p>
        <p>
          Average Likes/Note:{" "}
          {Math.round(
            (parseFloat(report?.likes) / parseFloat(report?.notes)) * 100
          ) / 100}
        </p>
        <p>
          Average Likes/User:{" "}
          {Math.round(
            (parseFloat(report?.likes) / parseFloat(report?.users)) * 100
          ) / 100}
        </p>
      </div>
      <div className="bg-primary-50 justify-center items-center flex flex-col w-full rounded-lg p-2 mt-10">
        <p className="text-xl">Data By Users:</p>
        <div className="w-full flex flex-row justify-between border-t border-l border-r border-black p-1">
          <p className="w-24 ">username</p>
          <p>Total Notes</p>
          <p>Total Likes</p>
          <p>Average Likes/Note</p>
        </div>
        <div className="w-full">
          {users?.map((user) => (
            <div className="flex flex-row justify-between border-t border-l border-r border-black p-1">
              <p className="w-24">{user.username}</p>
              <p className="w-20">{user.notes.length}</p>
              <p className="w-20">
                {user.notes.reduce((acc, note) => acc + note.likes.length, 0)}
              </p>
              <p className="w-20">
                {user.notes.length !== 0 &&
                user.notes.reduce((acc, note) => acc + note.likes.length, 0) !==
                  0
                  ? user.notes.length /
                    user.notes.reduce((acc, note) => acc + note.likes.length, 0)
                  : 0}
              </p>
            </div>
          ))}
          <div className="w-full flex flex-row justify-between border border-black p-1">
            <p className="w-24">totals</p>
            <p></p>
            <p></p>
            <p></p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Report;
